    &finally; block being run when an Exception
    has been thrown



<?php
function generate()
{
    try {
        yield 1;
        yield 2;
    } catch(\Exception $e) {
        echo 'catch';
    } finally {
        echo "finally\n";
    }
}

try {
    foreach (generate() as $i) {
        echo $i, "\n";
        throw new Exception();
    }
} catch(Exception $e) {
    echo 'outer catch';
}


   &example.outputs.similar;


