  int(3)
}
object(BcMath\Number)#4 (2) {
  ["value"]=>
  string(1) "3"
  ["scale"]=>
  int(0)
}

refsect1.example:
   Example of modulus operation using operators


<?php
$number = new BcMath\Number('-7.1');

$ret1 = $number % new BcMath\Number('-3.22');
$ret2 = $number % '7.1';
$ret3 = $number % 6;

var_dump($number, $ret1, $ret2, $ret3);
?>


   &example.outputs;


object(BcMath\Number)#1 (2) {
  ["value"]=>
  string(4) "-7.1"
  ["scale"]=>
  int(1)
}
object(BcMath\Number)#3 (2) {
  ["value"]=>
  string(5) "-0.66"
  ["scale"]=>
  int(2)
}
object(BcMath\Number)#2 (2) {
  ["value"]=>
  string(3) "0.0"
  ["scale"]=>
  int(1)
}
object(BcMath\Number)#4 (2) {
  ["value"]=>
  string(4) "-1.1"
  ["scale"]=>
  int(1)
}

   Examples of shorthand modulus operation using operators


<?php
$number = new BcMath\Number('3');
$num1 = $number;

$num1 %= new BcMath\Number('2');

var_dump($number, $num1);
?>


   &example.outputs;


object(BcMath\Number)#1 (2) {
  ["value"]=>
  string(1) "3"
  ["scale"]=>
  int(0)
}
object(BcMath\Number)#3 (2) {
  ["value"]=>
  string(1) "1"
  ["scale"]=>
  int(0)
}
