refentry.refsect1:
&reftitle.returnvalues;


 Connection identifier, when process is successful. 



 &false;, when process is unsuccessful.

  &reftitle.examples;

   cubrid_connect_with_url url without properties example


<?php
$conn_url = "CUBRID:localhost:33000:demodb:public:123456";
$con = cubrid_connect_with_url($conn_url);

if ($con) {
   echo "connected successfully";
   $req =cubrid_execute($con, "insert into person values(1,'James')");

   if ($req) {
      cubrid_close_request($req);
      cubrid_commit($con);
   } else {
      cubrid_rollback($con);
   }
   cubrid_disconnect($con);
}
?>





   cubrid_connect_with_url url with properties example


<?php
$conn_url = "CUBRID:127.0.0.1:33000:demodb:public:123456:?althost=10.34.63.132:33088&rctime=100"
$con = cubrid_connect_with_url ($conn_url);

if ($con) {
   echo "connected successfully";
   $req =cubrid_execute($con, "insert into person values(1,'James')");

   if ($req) {
      cubrid_close_request($req);
      cubrid_commit($con);
   } else {
      cubrid_rollback($con);
   }
   cubrid_disconnect($con);
}
?>
