refentry.refsect1:
&reftitle.description;
resourceeio_fstat mixedfd intpri callablecallback mixeddata
eio_fstat returns file status information in result argument of callback

&reftitle.parameters;


  fd



   Stream, Socket resource, or numeric file descriptor.




  pri

  &eio.request.pri.values;



  callback

   &eio.callback.proto;



  data



   Arbitrary variable passed to callback.

  &reftitle.examples;

   eio_lstat example


<?php
// Create temporary file
$tmp_filename = dirname(__FILE__) ."/eio-file.tmp";
touch($tmp_filename);

/* Is called when eio_fstat() done */
function my_res_cb($data, $result) {
 // Should output array with stat info
 var_dump($result);

 if ($data['fd']) {
  // Close temporary file
  eio_close($data['fd']);
  eio_event_loop();
 }
 // Remove temporary file
 @unlink($data['file']);
}

/* Is called when eio_open() done */
function my_open_cb($data, $result) {
 // Prepare data for callback
 $d = array(
  'fd'  => $result,
  'file'=> $data
 );
 // Request stat info
 eio_fstat($result, EIO_PRI_DEFAULT, "my_res_cb", $d);
 // Process request(s)
 eio_event_loop();
}

// Open temporary file
eio_open($tmp_filename, EIO_O_RDONLY, NULL, EIO_PRI_DEFAULT,
  "my_open_cb", $tmp_filename);
eio_event_loop();
?>


