refentry.refsect1:
&reftitle.description;
resourceeio_stat stringpath intpri mixedcallback mixeddataNULL
eio_stat returns file status information in result argument of callback

&reftitle.parameters;


  path



   The file path 




  pri

   &eio.request.pri.values;



  callback

   &eio.callback.proto;



  data



   Arbitrary variable passed to callback.

  &reftitle.examples;

   eio_stat example


<?php
$tmp_filename = "eio-file.tmp";
touch($tmp_filename);

function my_res_cb($data, $result) {
	var_dump($data);
	var_dump($result);
}

function my_open_cb($data, $result) {
	eio_close($result);
	eio_event_loop();

	@unlink($data);
}

eio_stat($tmp_filename, EIO_PRI_DEFAULT, "my_res_cb", "eio_stat");
eio_open($tmp_filename, EIO_O_RDONLY, NULL, 
 EIO_PRI_DEFAULT, "my_open_cb", $tmp_filename);
eio_event_loop();
?>


