refentry.refsect1:
  &reftitle.examples;


    locale_lookup example



<?php
$arr = array(
	'de-DEVA',
	'de-DE-1996',
	'de',
	'de-De'
);
echo locale_lookup($arr,  'de-DE-1996-x-prv1-prv2', false, 'en_US');
?>




   OO example


<?php
$arr = array(
	'de-DEVA',
	'de-DE-1996',
	'de',
	'de-De'
);
echo Locale::lookup($arr,  'de-DE-1996-x-prv1-prv2', false, 'en_US');
?>



  &example.outputs;


	de_de_1996
