refentry.refsect1:
  &reftitle.description;

&style.procedural;

   intmaxdb_get_proto_info
   resourcelink


&style.oop;

   maxdb
   stringprotocol_version



   Returns an integer representing the MaxDB protocol version used by the
   connection represented by the link parameter.

  &reftitle.examples;

   Object oriented style


<?php
$maxdb = new maxdb("localhost", "MONA", "RED", "DEMODB");

/* check connection */
if (maxdb_connect_errno()) {
   printf("Connect failed: %s\n", maxdb_connect_error());
   exit();
}

/* print protocol version */
printf("Protocol version: %d\n", $maxdb->protocol_version);

/* close connection */
$maxdb->close();
?>




   Procedural style


<?php
$link = maxdb_connect("localhost", "MONA", "RED", "DEMODB");

/* check connection */
if (maxdb_connect_errno()) {
   printf("Connect failed: %s\n", maxdb_connect_error());
   exit();
}

/* print protocol version */
printf("Protocol version: %d\n", maxdb_get_proto_info($link));

/* close connection */
maxdb_close($link);
?>



  &example.outputs.similar;


Protocol version: 10
