  &reftitle.examples;

   MongoCollection::batchInsert example


    Batch insertion is a quick way to add many elements to the database at once



<?php
$batch = array();
for ($i=0; $i<100; $i++) {
  $batch[] = array("i" => i);
}

$m = new Mongo();
$c = $m->selectCollection("foo", "bar.baz");
$c->batchInsert($batch);

$cursor = $c->find()->sort(array("i" => 1));
while ($cursor->hasNext()) {
  $obj = $cursor->next();
  var_dump($obj["i"]);
}
?>


