     &Version;
     &Description;





     8.3.0

      Now emits E_WARNING when token is not provided.

  &reftitle.examples;



    strtok example


<?php
$string = "This is\tan example\nstring";
/* Use tab and newline as tokenizing characters as well  */
$tok = strtok($string, " \n\t");

while ($tok !== false) {
    echo "Word=$tok<br />";
    $tok = strtok(" \n\t");
}
?>







    strtok behavior on empty part found


<?php
$first_token  = strtok('/something', '/');
$second_token = strtok('/');
var_dump($first_token, $second_token);
?>


    &example.outputs;


    string(9) "something"
    bool(false)







    The difference between strtok and explode


<?php
$string = ";aaa;;bbb;";

$parts = [];
$tok = strtok($string, ";");
while ($tok !== false) {
    $parts[] = $tok;
    $tok = strtok(";");
}
echo json_encode($parts),"\n";

$parts = explode(";", $string);
echo json_encode($parts),"\n";


    &example.outputs;


["aaa","bbb"]
["","aaa","","bbb",""]
