refentry.refsect1:
&reftitle.description;

 booltidyNode::isComment




 Tells if the node is a comment.

  &reftitle.examples;



    Extract comments from a mixed HTML document


<?php

$html = <<< HTML
<html><head>
<?php echo '<title>title</title>'; ?>
<# 
  /* JSTE code */
  alert('Hello World'); 
#>
</head>
<body>

<?php
  // PHP code
  echo 'hello world!';
?>

<%
  /* ASP code */
  response.write("Hello World!")
%>

<!-- Comments -->
Hello World
</body></html>
Outside HTML
HTML;


$tidy = tidy_parse_string($html);
$num = 0;

get_nodes($tidy->html(), 'comment');

function get_nodes($node, $node_type) {

    // check if the current node is of requested type
    if($node->{"is$node_type"}()) {
        echo "\n\n# $node_type node #" . ++$GLOBALS['num'] . "\n";
        echo $node->value;
    }

    // check if the current node has childrens
    if($node->hasChildren()) {
        foreach($node->child as $child) {
            get_nodes($child, $node_type);
        }
    }
}

?>


    &example.outputs;


# jste node #1
<# 
  /* JSTE code */
  alert('Hello World'); 
#>

