refentry.refsect1:
&reftitle.description;
public stringYaf_Route_Static::assemble arrayinfo arrayquery
Assemble a url.

&reftitle.errors;


 Throws Yaf_Exception_TypeError if info 
 keys ':c' and ':a' are not set.

  &reftitle.examples;

   Yaf_Route_Static::assembleexample


<?php

$router = new Yaf_Router();

$route  = new Yaf_Route_Static();

$router->addRoute("static", $route);

var_dump($router->getRoute('static')->assemble(
            array(
                ':a' => 'yafaction',
                'tkey' => 'tval',
                ':c' => 'yafcontroller',
                ':m' => 'yafmodule'
            ),
        )
);

var_dump($router->getRoute('static')->assemble(
            array(
                ':a' => 'yafaction',
                'tkey' => 'tval',
                ':c' => 'yafcontroller',
                ':m' => 'yafmodule'
            ),
            array(
                'tkey1' => 'tval1',
                'tkey2' => 'tval2'
            )
        )
);



   &example.outputs.similar;


string(%d) "/yafmodule/yafcontroller/yafaction"
string(%d) "/yafmodule/yafcontroller/yafaction?tkey1=tval1&tkey2=tval2"
